import React, {useRef, useState} from 'react';
import Axios from 'axios';

function SearchFormState(){

	const searchRef=useRef();
	const resultRef=useRef();

	const [searchVal, updateSearch] = useState("");


	/*
	function handleChange(e){
		const str = searchRef.current.value;
		if(str.length === 0){
			resultRef.current.innerHTML="";
			resultRef.current.style.display="none";
		}
		else{
			Axios.get(`http://localhost:5000/api/get/${str}`).then((response) => {
				if(response.data.length > 0){
					console.log(response.data[0].product_name);
					let inner = response.data.reduce((acc, value) => {
						console.log(value);
						return `${acc} ${value.product_name} <br/>`;
					}, "");

					resultRef.current.style.display="block";
					resultRef.current.innerHTML=inner;
					resultRef.current.style.border="1px solid #A5ACB2";
				}
			});	
		}
	}*/

	function handleSearchChange(event){
		updateSearch({searchVal: event.target.value});

	}	



	return(
		<>
			<form>
				<label>
					Search:
					<input type='text' value={searchVal} onChange={handleSearchChange}/>
					<div id="livesarch" ref={resultRef}></div> 
				</label>
			</form>
		</>
	);

}

export default SearchFormState;
