import React, {useState, useEffect} from 'react';
import {useAuth} from './AuthContext';
import {useNavigate} from 'react-router-dom';
import './App.css';
import LandingNavBar from './LandingNavBar';
import HomeNavBar from './HomeNavBar';

function App() {
	const navigate = useNavigate();
	const homeNavBar = <HomeNavBar/>;
	const landingNavBar = <LandingNavBar/>;
	const {currentUser, isLoggedIn}= useAuth();
	const [currentPage, setCurrentPage] = useState(<LandingNavBar/>);


	if(isLoggedIn && currentUser > 0 && currentPage !== homeNavBar){
		setCurrentPage(homeNavBar);
		//navigate("/");

	}
	else {
		setCurrentPage(landingNavBar);
	}

	return (
		<div className='App'>
			{currentPage}
		</div>
	);
}

export default App;
