import React, {useRef, useState} from 'react';
import Axios from 'axios';

function SearchFormControlled(){


	const [search, updateSearch] = useState("");
	const [result, updateResult] = useState();
	const [resultVisual, updateResultVisual] = useState({display: "none"});

	function handleSearchChange(e){
		console.log(resultVisual);
		updateSearch(e.target.value);
		const str = e.target.value;
		if(str.length === 0){
			//resultRef.current.innerHTML="";
			updateResultVisual({display:"none"});
		}
		else{
			Axios.get(`http://localhost:5000/api/get/${str}`).then((response) => {
				if(response.data.length > 0){
					console.log(response.data[0].product_name);
					let inner = response.data.reduce((acc, value) => {
						console.log(value);
						return `${acc} ${value.product_name} <br/>`;
					}, "");
					updateResult(response.data[0].product_name);	
					updateResultVisual({display:"block"}); /*
			*/	}
				else{
					console.log('nope');
					updateResultVisual({display:"none"});
				}
			});	
		}
	}




	return(
		<>
			<form>
				<label>
					Search:
					<input type='text' value={search} onChange={handleSearchChange}/>
					<div id="livesarch" style={{border:"1px solid #A5ACB2", display:resultVisual.display}} >{result}</div> 
				</label>
			</form>
		</>
	);

}

export default SearchFormControlled;
