import Axios from 'axios';
import e from 'express';
import React, {useState, useContext} from 'react';


const AuthContext = React.createContext();
export function useAuth() {
    return useContext(AuthContext)
}
export default function AuthProvider({children}){
        const [currentUser, setCurrentUser] = useState();

        function signup(username, password){
            // Signup here
        }

        function login(username, password){
            // Query server with POST
            // Get back session key and user_id if valid
            const request = {'username': username, 'password': password};
            Axios.post("http://db8.cse.nd.edu/cse30246/bacND/server/postLogin.php", request).then((response) =>{
                if(response.data){
                   return response.data 
                }
                else{
                    // Return an error message or something for when the password is wrong
                }
            }).catch((error) => {
                console.log(error);
            })


            setCurrentUser(14);
        }

        /*
        useEffect(() => {
            setCurrentUser()
        }, []);
        */

        const value = {
            currentUser,
            signup, 
            login
        };

            return (
                <AuthContext.Provider value={value}>
                    {children}
                </AuthContext.Provider>

            );


}
